import React, { useEffect } from "react";
import styles from "./Modal.module.css";
import ReactDOM from "react-dom";
import { CloseIcon } from "@ya.praktikum/react-developer-burger-ui-components";

const modalRoot = document.getElementById("react-modal");

export default function Modal({ setIsModalOpen, itemIng }) {
  return ReactDOM.createPortal(
    <div className={styles.modal}>
      <IngredientsDetails setIsModalOpen={setIsModalOpen} itemIng={itemIng} />
    </div>,
    modalRoot
  );
}

export function Modalion({ setIsModalOpen }) {
  return ReactDOM.createPortal(
    <div className={styles.modal}>
      <OrderDetails setIsModalOpen={setIsModalOpen} />
    </div>,
    modalRoot
  );
}

function IngredientsDetails({ setIsModalOpen, itemIng }) {
  useEffect(() => {
    function onEsc(evt) {
      if (evt.code === "Escape") {
        setIsModalOpen(false);
      }
    }
    document.addEventListener("keydown", onEsc);
    return () => document.removeEventListener("keydown", onEsc);
  }, []);

  const closer = () => {
    setIsModalOpen(false);
  };

  return (
    <div className={styles.box + " pt-10"}>
      <div className={styles.title}>
        <p className=" text text_type_main-large">Детали ингредиента</p>
        <CloseIcon type="primary" onClick={closer} />
      </div>
      <img src={itemIng.image_large} className="pb-4" />
      <p className="text text_type_main-medium pb-8">{itemIng.name}</p>
      <div
        className={
          styles.row + " text text_type_main-default text_color_inactive"
        }
      >
        <div className={styles.str + " mr-5"}>
          <span>Калории,калл</span>
          <span>{itemIng.calories}</span>
        </div>
        <div className={styles.str + " mr-5"}>
          <span>Белки, г</span>
          <span>{itemIng.proteins}</span>
        </div>
        <div className={styles.str + " mr-5"}>
          <span>Жиры, г</span>
          <span>{itemIng.fat}</span>
        </div>
        <div className={styles.str}>
          <span>Углеводы, г</span>
          <span>{itemIng.carbohydrates}</span>
        </div>
      </div>
    </div>
  );
}

function OrderDetails({ setIsModalOpen }) {
  useEffect(() => {
    function onEsc(evt) {
      if (evt.code === "Escape") {
        setIsModalOpen(false);
      }
    }
    document.addEventListener("keydown", onEsc);
    return () => document.removeEventListener("keydown", onEsc);
  }, []);

  const closer = () => {
    setIsModalOpen(false);
  };

  return (
    <div className={styles.box + " pt-10"}>
      <CloseIcon type="primary" onClick={closer} />
      <p className=" text text_type_main-large">Детали ингредиента</p>
      <p className="text text_type_main-medium pb-8">Идентификатор заказа</p>
      <img src="" />
      <span>Ваш заказ начали готовить</span>
      <span>Дождитесь готовности на орбитальной станции</span>
    </div>
  );
}
