import styles from "./app.module.css";
import AppHeader from "../AppHeader/AppHeader";
import AppMain from "../AppMain/AppMain";
import React, { useEffect, useState } from "react";
import Modal from "../Modal/Modal";
import Overlay from "../Overlay/Overlay";

function App() {
  const [isLoading, setIsloading] = useState(false);
  const [ingridientList, setingridientList] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [itemIng, setItemIng] = useState();

  useEffect(() => {
    setIsloading(true);
    fetch("https://norma.nomoreparties.space/api/ingredients").then((res) => {
      res.json().then((data) => {
        setingridientList(data.data);
      });
    });
  }, []);

  return (
    <div className={styles.app}>
      <AppHeader />
      <AppMain data={ingridientList} setIsModalOpen={setIsModalOpen} />
      {isModalOpen && (
        <>
          <Modal setItemIng={setItemIng} setIsModalOpen={setIsModalOpen} />
          <Overlay setIsModalOpen={setIsModalOpen} />
        </>
      )}
    </div>
  );
}

export default App;
