import styles from "./app.module.css";
import AppHeader from "../AppHeader/AppHeader";
import AppMain from "../AppMain/AppMain";
import React, { useEffect, useState } from "react";
import Modal from "../Modal/Modal";
import Overlay from "../Overlay/Overlay";
import { IngredientsDetails, OrderDetails } from "../Modal/Modal";
import BurgerIngredients from "../BurgerIngredients/BurgerIngredients";

function App() {
  const [ingridientList, setingridientList] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isConstructorOpen, setIsConstructorOpen] = useState(false);
  const [itemIng, setItemIng] = useState();
  const [itemOrder, setItemOrder] = useState();

  useEffect(() => {
    fetch("https://norma.nomoreparties.space/api/ingredients").then((res) => {
      res.json().then((data) => {
        setingridientList(data.data);
      });
    });
  }, []);

  const childForModal = <IngredientsDetails itemIng={itemIng} />;
  const childForMod = <OrderDetails />;

  return (
    <div className={styles.app}>
      <AppHeader />
      <AppMain
        data={ingridientList}
        setItemIng={setItemIng}
        setIsModalOpen={setIsModalOpen}
        setItemOrder={setItemOrder}
      />
      {isModalOpen && (
        <>
          <Modal setIsModalOpen={setIsModalOpen}>
            {() => {
              if (evt.target !== BurgerIngredients) {
                return childForMod;
              } else if (evt.target !== BurgerConstructor) {
                return childForModal;
              }
            }}
          </Modal>
          <Overlay setIsModalOpen={setIsModalOpen} />
        </>
      )}
    </div>
  );
}

export default App;
