import styles from "./app.module.css";
import AppHeader from "../AppHeader/AppHeader";
import AppMain from "../AppMain/AppMain";
import React, { useEffect, useState } from "react";
import Modal from "../Modal/Modal";
import Overlay from "../Overlay/Overlay";

function App() {
  const [isLoading, setIsloading] = useState(false);
  const [ingridientList, setingridientList] = useState([]);
  const [isModal, setIsModalOpen] = useState(false);

  useEffect(() => {
    setIsloading(true);
    fetch("https://norma.nomoreparties.space/api/ingredients").then((res) => {
      res.json().then((data) => {
        setingridientList(data.data);
      });
      // setIsloading(false);
      // setingridientList(res.data);
      // console.log(res);
    });
    console.log(ingridientList);
  }, []);
  console.log(ingridientList);
  // if (isLoading) {
  //   return <Preloader />;
  // }

  return (
    <div className={styles.app}>
      <AppHeader />
      <AppMain data={ingridientList} />
      <Modal ingredient={ingridientList} setIsModalOpen={setIsModalOpen} />
      <Overlay setIsModalOpen={setIsModalOpen} />
    </div>
  );
}

export default App;
