import { Bun, Main, Sauce } from "../Ingredient/Ingredient";
import styles from "./BurgerIngredients.module.css";
import React from "react";
import { Tab } from "@ya.praktikum/react-developer-burger-ui-components";

export default function BurgerIngredients({ data, setIsModalOpen }) {
  const onClick = () => {
    setIsModalOpen(true);
  };

  return (
    <div className={styles.box + " mt-10"}>
      <span className=" text text_type_main-large">Соберите бургер</span>
      <div className="mt-5">
        <BurgerComponents />
        <div className={styles.ingBox + " custom-scroll"}>
          <div className="mt-10">
            <Bun
              ingredients={data}
              ingType={"Булки"}
              setIsModalOpen={setIsModalOpen}
              onClick={onClick}
            />
          </div>
          <div className="mt-10">
            <Sauce
              ingredients={data}
              ingType={"Соусы"}
              setIsModalOpen={setIsModalOpen}
              onClick={onClick}
            />
          </div>
          <div className="mt-10">
            <Main
              ingredients={data}
              ingType={"Начинка"}
              setIsModalOpen={setIsModalOpen}
              onClick={onClick}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

function BurgerComponents() {
  const [current, setCurrent] = React.useState("Булки");
  return (
    <div style={{ display: "flex" }}>
      <Tab value="Булки" active={current === "Булки"} onClick={setCurrent}>
        Булки
      </Tab>
      <Tab value="Соусы" active={current === "Соусы"} onClick={setCurrent}>
        Соусы
      </Tab>
      <Tab value="Начинки" active={current === "Начинки"} onClick={setCurrent}>
        Начинки
      </Tab>
    </div>
  );
}
