import {
  CurrencyIcon,
  Counter,
} from "@ya.praktikum/react-developer-burger-ui-components";
import styles from "./Ingredient.module.css";
import { data } from "../../utils/data";

export function Bun({ ingredients, ingType, setIsModalOpen }) {
  return (
    <div>
      <p className="text text_type_main-medium">{ingType}</p>
      <div className={styles.grid + " pt-6"}>
        {ingredients.map((item) => {
          if (item.type == "bun") {
            return (
              <ProtoIngredient
                key={item._id}
                ingredient={item}
                setIsModalOpen={setIsModalOpen}
              />
            );
          }
        })}
      </div>
    </div>
  );
}

export function Sauce({ ingredients, ingType, setIsModalOpen }) {
  return (
    <div>
      <p className="text text_type_main-medium">{ingType}</p>
      <div className={styles.grid + " pt-6"}>
        {ingredients.map((item) => {
          if (item.type == "sauce") {
            return (
              <ProtoIngredient
                key={item._id}
                ingredient={item}
                setIsModalOpen={setIsModalOpen}
              />
            );
          }
        })}
      </div>
    </div>
  );
}

export function Main({ ingredients, ingType, setIsModalOpen }) {
  return (
    <div>
      <p className="text text_type_main-medium">{ingType}</p>
      <div className={styles.grid + " pt-6"}>
        {ingredients.map((item) => {
          if (item.type == "main") {
            return (
              <ProtoIngredient
                key={item._id}
                ingredient={item}
                setIsModalOpen={setIsModalOpen}
              />
            );
          }
        })}
      </div>
    </div>
  );
}

function ProtoIngredient({ ingredient, setIsModalOpen }) {
  const onClick = () => {
    setIsModalOpen(true);
  };

  return (
    <div className={styles.ingredient}>
      <Counter count={1} size="default" extraClass="m-1" />
      <img className="ml-4 mr-4" src={ingredient.image} onClick={onClick} />
      <div className={styles.block + " mt-1 mb-1"}>
        <span className="text text_type_digits-default mr-1">
          {ingredient.price}
        </span>
        <CurrencyIcon type="primary" />
      </div>
      <p className={styles.center + " text text_type_main-small"}>
        {ingredient.name}
      </p>
    </div>
  );
}
