import React, { useEffect } from "react";
import styles from "./Modal.module.css";
import ReactDOM from "react-dom";
import { CloseIcon } from "@ya.praktikum/react-developer-burger-ui-components";

const modalRoot = document.getElementById("react-modal");

export default function Modal({ setIsModalOpen, itemIng }) {
  useEffect(() => {
    function onEsc(evt) {
      if (evt.code === "Escape") {
        setIsModalOpen(false);
      }
    }
    document.addEventListener("keydown", onEsc);
    return () => document.removeEventListener("keydown", onEsc);
  }, []);

  return ReactDOM.createPortal(
    <div className={styles.modal}>
      <div className={styles.box + " pt-10"}>
        <div className={styles.title}>
          <p className=" text text_type_main-large">Детали ингредиента</p>
          <CloseIcon type="primary" />
        </div>
        <img src={itemIng.image_large} className="pb-4" />
        <p className="text text_type_main-medium pb-8">{itemIng.name}</p>
        <div className="text text_type_main-default text_color_inactive">
          <span className={styles.str}>Калории, калл{itemIng.calories}</span>
          <span className={styles.str}>Белки, г{itemIng.proteins}</span>
          <span className={styles.str}>Жиры, г{itemIng.fat}</span>
          <span className={styles.str}>
            Углеводы, г {itemIng.carbohydrates}
          </span>
        </div>
      </div>
    </div>,
    modalRoot
  );
}
