import CurrencyIcon from "@ya.praktikum/react-developer-burger-ui-components";
import styles from "./Ingredient.module.css";
import { data } from "../../utils/data.js";
export function Ingredient({ ingredients, type }) {
  return (
    <div>
      <p>{type}</p>
      {ingredients.map((item) => (
        <ProtoIngredient key={item._id} ingredient={item} />
      ))}
    </div>
  );
}

function ProtoIngredient({ ingredient }) {
  return (
    <div className={styles.ingredient}>
      <img className={styles.image} src={ingredient.image} />
      <div>
        <span className={styles.price}>{ingredient.price}</span>
        {/* <CurrencyIcon type="primary" /> */}
      </div>
      <p className={styles.name}>{ingredient.name}</p>
    </div>
  );
}
